// Get a random word
function generateRandomWord () {
    randomWord = wordList[Math.floor(Math.random() * wordList.length)]
} 
generateRandomWord()
console.log(randomWord)

//Keyboard

function guessedWord () {
    guess = ''
}
guessedWord()

let letterCounter = 0

function onScreenKeyboardFunctionGuess () {
    let keys = document.getElementsByClassName('key');
    for (let keyElement of keys) {
        let key = keyElement.textContent;
        keyElement.addEventListener('click', function() {
            let id = letterCounter+1
            let output = document.getElementById(id);
            // console.log(id)
            switch (key) {
                case 'Backspace':
                    let removeOutput = document.getElementById(letterCounter)
                    removeOutput.textContent =``;
                    guess = guess.slice(0,-1)
                    letterCounter--
                    break;
                default:
                    if (letterCounter === 5 || letterCounter===11 || letterCounter === 17 || letterCounter === 23 || letterCounter === 29) {
                    break
                    }
                    output.textContent += key;
                    guess +=`${output.textContent}`
                    guess = guess.replaceAll(' ', '')
                    letterCounter ++
                }
                // console.log(guess)      
        }) 
    }
}
onScreenKeyboardFunctionGuess()

let scoreCounter = 0
let score = document.querySelector('.score')
score.innerText = scoreCounter

let highScoreCounter = 0
let highScore = document.querySelector('.highScore')
highScore.innerText = highScoreCounter

// Win Function

function win() {
if (guess.toLowerCase() === randomWord){
    document.querySelector('.result').classList.add("win")
    document.querySelector('.win').innerText = "WINNER";
    document.querySelector('.the-results').style.display = "block";
    document.querySelector('.display-result').style.display = "block";
    document.getElementById("playagain").style.display = "block";
    }  
}

function pinkWinXinYu() {
    let xinyubox = document.querySelectorAll("#submitted")
    if (guess.toLowerCase() === "xinyu") {
        for (let i=xinyubox.length-6; i<xinyubox.length-1; i++) {
            xinyubox[i].id = i+1
            xinyubox[i].parentElement.style.backgroundColor = '#ff99e6'
            xinyubox[i].classList.add('spintiles')
        } 
    }
    setTimeout(winXinYu, 700)
}

function winXinYu() {
    if (guess.toLowerCase() === "xinyu"){
        console.log(guess)

        document.querySelector('.result').classList.add("win")
        document.querySelector('.win').innerText = "WINNER"
        
        document.querySelector('.win-xinyu-text').innerText = "You found the special word!"

        document.querySelector('.plus-points').innerText = "+10,000"
        scoreCounter = scoreCounter + 10000
        score.innerText = scoreCounter

        currentScoreDisplay.innerText = `${scoreCounter}pts`
        document.querySelector('.the-results').style.display = "block";
        document.querySelector('.display-result').style.display = "block";
        document.getElementById("playagain").style.display = "block";
        wordList.pop("xinyu")
    }  
}

// Lose Function
function lose() {
    if ((guess.toLowerCase() !== randomWord) && (letterCounter === 36)){
        document.querySelector('.result').classList.add("lost")
        if (scoreCounter > highScoreCounter) {
            highScoreCounter = scoreCounter
            highScore.innerText = highScoreCounter
            document.querySelector('.lost').innerText = "Game Over"
            document.querySelector('.plus-points').classList.add("new-high-score")
            document.querySelector('.new-high-score').innerText = "Wow! New High Score!"
            currentScoreDisplay.innerText = `${highScoreCounter}pts`
        } else {
            document.querySelector('.lost').innerText = "Game Over"
            currentScoreDisplay.innerText = `${scoreCounter}`
        }
        document.querySelector('.the-results').style.display = "block";
        document.querySelector('.display-result').style.display = "block";
        document.getElementById("playagain").style.display = "block";
        let revealWord = document.querySelector(".win-xinyu-text")
        revealWord.innerText = `The word was "${randomWord.toUpperCase()}"`
    } 
    scoreCounter = 0
    score.innerText = scoreCounter
}

// Play again buttons
const playAgainButton = document.getElementById("playagain")
playAgainButton.addEventListener('click', playAgain)
playAgainButton.addEventListener('click', playAgainButtonHide)


const displayResult = document.querySelector(".display-result")
const closeResult = document.querySelector(".close-result")
const theResults = document.querySelector(".the-results")

const onScreenPlayAgain = document.querySelector(".play-again-onscreen")
onScreenPlayAgain.addEventListener('click', playAgain)
onScreenPlayAgain.addEventListener('click', playAgainButtonHide)

function playAgainButtonHide () {
    onScreenPlayAgain.style.display = "none";
    if (mediaQuery.matches) {document.querySelector(".instruction-button").style.marginTop = "170px"}
}

// Results Functions 
const mediaQuery = window.matchMedia('(min-width: 600px)')

closeResult.addEventListener('click', function () {
    displayResult.style.display = "none";
    theResults.style.display = "none";
    playAgainButton.style.display = "none";
    onScreenPlayAgain.style.display = "block";

    if (mediaQuery.matches) {document.querySelector(".instruction-button").style.marginTop = "10px"}
})

theResults.addEventListener('click', function () {
    displayResult.style.display = "none";
    theResults.style.display = "none";
    playAgainButton.style.display = "none";
    onScreenPlayAgain.style.display = "block";
    
    if (mediaQuery.matches) {document.querySelector(".instruction-button").style.marginTop = "10px"}
})

function playAgain () {
    guess = ''
    let box = document.querySelectorAll(".output")
    for (let i=0; i<box.length; i++) {
        box[i].id = i+1
        box[i].innerText = ''
        box[i].parentElement.style.backgroundColor = ''
        box[i].classList.add('fliptiles')
    }
    generateRandomWord()
    console.log(randomWord)
    document.querySelector('.result').classList.remove('win')
    document.querySelector('.result').classList.remove('lost')
    document.querySelector('.result').classList.remove("winXinYu")
    document.querySelector('.plus-points').innerText = ''
    document.querySelector('.result').innerText = ''
    document.querySelector(".current-score").innerText = ''
    document.querySelector('.win-xinyu-text').innerText = ''
    document.getElementById('playagain').style.display = "none";
    displayResult.style.display = "none";
    theResults.style.display = "none";
    letterCounter = 0
    submitFunction = 0
} 

const resetButton = document.getElementById("reset")
resetButton.addEventListener('click', resetGame)
function resetGame() {
   playAgain()
   scoreCounter = 0
   score.innerText = scoreCounter
   highScoreCounter = scoreCounter
   highScore.innerText = highScoreCounter
   wordList.push("xinyu")
} 

// Instruction Functions
const instructionButton = document.querySelector(".instruction-button")
const instructions = document.querySelector(".instructions")
const theInstructions = document.querySelector(".the-instructions")

// Scroll code referenced from  https://gist.github.com/sabapathygithub/e6ca2c0fd06c21c5fb608b9a172ca3c4

instructionButton.addEventListener('click', function() {
    instructions.style.display = "block";
    theInstructions.style.display = "block";
    function scrollToBottom() {
        let scrollId;
        let height = 0;
        let minScrollHeight = 100;
        scrollId = setInterval(function () {
            if (height <= document.body.scrollHeight) {
                window.scrollBy(0, minScrollHeight);
            }
            height += minScrollHeight;
        }, 0);           
    } 
    scrollToBottom()
})

const closeInstructions = document.querySelector(".close-instructions")
closeInstructions.addEventListener('click', function() {
    instructions.style.display = "none";
    theInstructions.style.display = "none";
})

theInstructions.addEventListener('click', function() {
    instructions.style.display = "none";
    theInstructions.style.display = "none";
})

// Sound Button

const sound = document.querySelector('.settings-button')
const buttons = document.querySelectorAll('button')
const submitButton = document.getElementById('submit')

soundOn()

sound.addEventListener('click', toggleSound)
function toggleSound () {
    if (sound.innerText === "Sound On") {
        sound.innerText = "Sound Off"
        soundOff()
    } else if (sound.innerText === "Sound Off") {
        sound.innerText = "Sound On"
        soundOn()
    }
}
function soundOff () {
    buttons.forEach(button => {
    button.addEventListener('click', function() {
        document.querySelector('.clickSound').pause()
    })
    submitButton.addEventListener('click', () => document.querySelector('.submitButtonSound').pause())
    })
}

function soundOn () {
    buttons.forEach(button => {
    button.addEventListener('click', function() {
        document.querySelector('.clickSound').play()
    })
    submitButton.addEventListener('click', () => document.querySelector('.submitButtonSound').play())
    })
} 

// Score Display

const scoreDisplay = document.querySelector(".plus-points")
const currentScoreDisplay = document.querySelector(".current-score")
function pointsCounter () {
    if (guess.toLowerCase() === randomWord){
        if (letterCounter === 6) {
            scoreCounter = scoreCounter + 1200
            scoreDisplay.innerText = "+1200"
        } else if (letterCounter === 12) {
            scoreCounter = scoreCounter + 900
            scoreDisplay.innerText = "+900"
        } else if (letterCounter === 18) {
            scoreCounter = scoreCounter + 550
            scoreDisplay.innerText = "+550"
        } else if (letterCounter === 24) {
            scoreCounter = scoreCounter + 300
            scoreDisplay.innerText = "+300"
        } else if (letterCounter === 30) {
            scoreCounter = scoreCounter + 100
            scoreDisplay.innerText = "+100"
        } else if (letterCounter === 36) {
            scoreCounter = scoreCounter + 50
            scoreDisplay.innerText = "+50"
        }
    }
    score.innerText = scoreCounter
    currentScoreDisplay.innerText = `${scoreCounter} pts`
}

